import "../App.css";
import { Collapse, Divider, List, Input, Checkbox, Button } from "antd";
import React, { Component } from "react";
import {FB} from './questionsData'
const { Panel } = Collapse;


var prof_length = FB.Professionalism.length;
var stfhyg_length = FB.Staff_Hygiene.length;
var prof_cat_size = prof_length+stfhyg_length;

var housekeeping_cat_size = FB.General_Environment_Cleanliness.length+FB.Hand_Hygiene_Facilities.length;

class Checklist extends Component {
    componentWillMount = () => {
        this.selectedCheckboxes = new Set();
      }
    
      toggleCheckbox = label => {
        if (this.selectedCheckboxes.has(label)) {
          this.selectedCheckboxes.delete(label);
        } else {
          this.selectedCheckboxes.add(label);
        }
      }
    
      handleFormSubmit = formSubmitEvent => {
        formSubmitEvent.preventDefault();
    
        for (const checkbox of this.selectedCheckboxes) {
          console.log(checkbox, 'is selected.');
        }
      }
    
      

      state={
          checked:false,
          profcount:0,
          stfhygcount:0,
          gec_count:0,
          handhyg_count: 0
      }

      createprofCheckbox = label => (
        <Checkbox
                label={label}
                handleCheckboxChange={this.toggleCheckbox}
                key={label}
                onChange = {e => this.handleprofCheckCount(e)}
            />
      )
      handleprofCheckCount=(e)=>{
          const {checked,type} = e.target;
          if(type === "checkbox" && checked === true) {
            this.setState(state => state.profcount++)
          } else {
            this.setState(state => state.profcount--)
          }
      }

      createstfhygCheckbox = label => (
        <Checkbox
                label={label}
                handleCheckboxChange={this.toggleCheckbox}
                key={label}
                onChange = {e => this.handlestfhygCheckCount(e)}
            />
      )
      handlestfhygCheckCount=(e)=>{
        const {checked,type} = e.target;
        if(type === "checkbox" && checked === true) {
          this.setState(state => state.stfhygcount++)
        } else {
          this.setState(state => state.stfhygcount--)
        }
    }

    creategecCheckbox = label => (
      <Checkbox
              label={label}
              handleCheckboxChange={this.toggleCheckbox}
              key={label}
              onChange = {e => this.handlegecCheckCount(e)}
          />
    )
    handlegecCheckCount=(e)=>{
      const {checked,type} = e.target;
      if(type === "checkbox" && checked === true) {
        this.setState(state => state.gec_count++)
      } else {
        this.setState(state => state.gec_count--)
      }
  }

  createhandhygCheckbox = label => (
    <Checkbox
            label={label}
            handleCheckboxChange={this.toggleCheckbox}
            key={label}
            onChange = {e => this.handlehandhygCheckCount(e)}
        />
  )
  handlehandhygCheckCount=(e)=>{
    const {checked,type} = e.target;
    if(type === "checkbox" && checked === true) {
      this.setState(state => state.handhyg_count++)
    } else {
      this.setState(state => state.handhyg_count--)
    }
}

      


 
render(){
    return (
        <>
            <h1 className="start">F&B Checklist</h1>



            <div>{FB.Professionalism.length}</div>
                        <div>{FB.Staff_Hygiene.length}</div>
                        <div>{this.state.profcount}</div>




            <Divider />

            <div>
                <Collapse accordion defaultActiveKey="1" >

                    <Panel header="Professionalism & Staff Hygiene (10%)" key="1" className="bg-orange text-white">

                        <Collapse accordion defaultActiveKey="1" >
                            <Panel header="Professionalism" key="1" className="bg-orange text-white">
                            <List
                            dataSource={FB.Professionalism} // Questions
                            renderItem={(item) => (
                              <List.Item>
                                <div className="create-audit-row">{item}</div>
                                {this.createprofCheckbox()}
                              </List.Item>
                            )}
                          />
                            </Panel>

                            <Panel header="Staff Hygiene" key="2" className="bg-orange text-white">
                            <List
                            dataSource={FB.Staff_Hygiene} // Questions
                            renderItem={(item) => (
                              <List.Item>
                                <div className="create-audit-row">{item}</div>
                                <div>{this.createstfhygCheckbox()}</div>
                              </List.Item>
                            )}
                          />
                            </Panel>
                        </Collapse>
                        
                        <div class="pt-10 font-bold text-right">Score: {Math.round(10/2/(prof_cat_size)*(this.state.profcount+this.state.stfhygcount))}/10</div>

                    </Panel>

                    <Panel header="Housekeeping & General Cleanliness (20%)" key="2" className="bg-orange text-white">
                        <Collapse accordion defaultActiveKey="1" >
                        <Panel header="General Environment Cleanness" key="1" className="bg-orange text-white">
                            <List
                            dataSource={FB.General_Environment_Cleanliness} // Questions
                            renderItem={(item) => (
                              <List.Item>
                                <div className="create-audit-row">{item}</div>
                                <div>{this.creategecCheckbox()}</div>
                              </List.Item>
                            )}
                          />
                            </Panel>
                            <Panel header="Hand Hygiene Facilities" key="2" className="bg-orange text-white">
                            <List
                            dataSource={FB.Hand_Hygiene_Facilities} // Questions
                            renderItem={(item) => (
                              <List.Item>
                                <div className="create-audit-row">{item}</div>
                                <div>{this.createhandhygCheckbox()}</div>
                              </List.Item>
                            )}
                          />
                            </Panel>


                        </Collapse>
                        <div class="pt-10 font-bold text-right">Score: {Math.round(20/2/(housekeeping_cat_size)*(this.state.gec_count+this.state.handhyg_count))}/20</div>
                    </Panel>

                    <Panel header="Food Hygiene ()" key="3" className="bg-orange text-white">

                        <Collapse accordion defaultActiveKey="1">
                            <Panel header="General Safety" key="1" className="bg-orange text-white">
                                <List
                                    // dataSource={dataNonFB.data3_1}
                                    renderItem={item => (
                                        <List.Item>
                                            <div className="row justify-between">
                                                {item}
                                                
                                            </div>
                                        </List.Item>
                                    )}
                                />
                            </Panel>

                            <Panel header="Fire & Emergency Safety" key="2" className="bg-orange text-white">
                                <List
                                    // dataSource={dataNonFB.data3_2}
                                    renderItem={item => (
                                        <List.Item>
                                            <div className="row justify-between">
                                                {item}
                                                
                                            </div>
                                        </List.Item>
                                    )}
                                />
                            </Panel>

                            <Panel header="Electrical Safety" key="3" className="bg-orange text-white">
                                <List
                                    // dataSource={dataNonFB.data3_3}
                                    renderItem={item => (
                                        <List.Item>
                                            <div className="row justify-between">
                                                {item}
                                                
                                            </div>
                                        </List.Item>
                                    )}
                                />
                            </Panel>
                        </Collapse>
                        <div class="pt-10 font-bold text-right">Score: __/40</div>
                    </Panel>
                </Collapse>
            </div>

           {/*  <div class="pt-20">
                <TextArea placeholder="Remarks" allowClear onChange={onChange} />
            </div>
            <Divider />
            <div class="row justify-between">
                <div>Professionalism & Staff Hygiene</div>
                <div>__/20%</div>
            </div>
            <div class="row justify-between">
                <div>Housekeeping & General Cleanliness</div>
                <div>__/40%</div>
            </div>
            <div class="row justify-between">
                <div>Workplace Safety & Health</div>
                <div>__/40%</div>
            </div>
            <div class="row justify-between font-bold">
                <div>Total</div>
                <div>__/100%</div>
            </div> */}
        </>

    )
            
}
}
export default Checklist;
