import React from "react";
import { Link, Route, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { ReactComponent } from "*.svg";

class notStaffPage extends ReactComponent {
  render() {}
}

const PrivateRoute = ({ component: Component, auth, ...rest }) => (
  <Route
    {...rest}
    render={(props) =>
      auth.isAuthenticated === true ? (
        auth.user.usertype === "staff" ? ( // check for staff usertype
          <Component {...props} />
        ) : (
          ((<h1>Sorry, you are not authorised to view this page</h1>),
          (
            <h3>
              If you are a Singhealth Staff member, please remember to check the
              Staff Member checkbox before loging in
            </h3>
          ),
          (<Link to="/login">Click here</Link>))
        )
      ) : (
        <Redirect to="/login" />
      )
    }
  />
);

PrivateRoute.propTypes = {
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps)(PrivateRoute);
