import "../App.css";
import { Collapse, Divider, List, Input, Checkbox, Button } from "antd";
import React, { Component } from "react";
import importJSON from "../data/questionsDict.json";
const Fb = importJSON.fb;
const { Panel } = Collapse;

// TODO: Take score from json file => Update the score, replace the json file

class Checklist extends Component {
  componentWillMount = () => {
    this.selectedCheckboxes = new Set();
  };

  toggleCheckbox = (label) => {
    if (this.selectedCheckboxes.has(label)) {
      this.selectedCheckboxes.delete(label);
    } else {
      this.selectedCheckboxes.add(label);
    }
  };

  handleFormSubmit = (formSubmitEvent) => {
    formSubmitEvent.preventDefault();

    for (const checkbox of this.selectedCheckboxes) {
      console.log(checkbox, "is selected.");
    }
  };

  createprofCheckbox = (label, catIndex) => (
    <Checkbox
      label={label}
      handleCheckboxChange={this.toggleCheckbox}
      key={label}
      onChange={(e) => this.handleCount(e, catIndex)}
    />
  );

  state = {
    checked: false,
    catCounts: [0, 0, 0, 0, 0], // counts[0]: for Professionalism & Staff Hygiene (10%), counts[1]: for Housekeeping & General Cleanliness (20%)
  };
  handleCount = (e, catIndex) => {
    const { checked, type } = e.target;
    switch (catIndex) {
      case 0:
        if (type === "checkbox" && checked === true) {
          this.setState((state) => state.catCounts[0]++);
        } else {
          this.setState((state) => state.cat0count--);
        }
      case 1:
        if (type === "checkbox" && checked === true) {
          this.setState((state) => state.cat1count++);
        } else {
          this.setState((state) => state.cat1count--);
        }
      case 2:
        if (type === "checkbox" && checked === true) {
          this.setState((state) => state.cat2count++);
        } else {
          this.setState((state) => state.cat2count--);
        }
      case 3:
        if (type === "checkbox" && checked === true) {
          this.setState((state) => state.cat3count++);
        } else {
          this.setState((state) => state.cat3count--);
        }
      case 4:
        if (type === "checkbox" && checked === true) {
          this.setState((state) => state.cat4count++);
        } else {
          this.setState((state) => state.cat4count--);
        }
    }
  };

  render() {
    return (
      <div className="panels">
        {Fb.map((cat, catIndex) => {
          // var catScore = cat.score;
          return (
            // Category
            <Collapse defaultActiveKey={["1"]}>
              <Panel
                header={<div catIndex={catIndex}>{cat.name}</div>}
                key={catIndex + 1}
                className="bg-orange"
              >
                <div catIndex={catIndex}>
                  {cat.subcategories.map((subCat, subCatIndex) => {
                    return (
                      // SubCategory
                      <Collapse defaultActiveKey={["1"]}>
                        <Panel
                          header={
                            <div subCatIndex={subCatIndex}>{subCat.name}</div>
                          }
                          key={subCatIndex + 1}
                          className="bg-orange"
                        >
                          <List
                            dataSource={subCat.questions} // Questions
                            renderItem={(item) => (
                              <List.Item>
                                <div className="create-audit-row">{item}</div>
                                <div>
                                  {this.createprofCheckbox(item, catIndex)}
                                </div>
                              </List.Item>
                            )}
                          />
                        </Panel>
                      </Collapse>
                    );
                  })}
                </div>
                <div>Score: {this.state.cat0count}</div>
              </Panel>
            </Collapse>
          );
        })}
        <div>{this.state.cat0count}</div>
        <Button className="submit-button" type="primary" htmlType="submit">
          SUBMIT
        </Button>
      </div>
    );
  }
}

export default Checklist;
