/* eslint-disable import/no-anonymous-default-export */
import { GET_MESSAGE } from "../actions/types";

const isEmpty = require("is-empty");

const initialState = {
  message: {},
};

// anonymous function
export default function (state = initialState, action) {
  switch (action.type) {
    case GET_MESSAGE:
      return {
        message: action.payload,
      };
    default:
      return state;
  }
}
