import { getAllByPlaceholderText } from "@testing-library/dom";
import axios from "axios";
import { GET_ERRORS, GET_MESSAGE } from "./types";
import { sendEmail } from "./authActions";
import User from "../../../models/Tenant";

export const submit = (data) => (dispatch) => {
  console.log("submit", data);
  axios
    .post("/api/audits/add", data)
    .then(() => {
      dispatch({
        type: GET_MESSAGE,
        payload: "Audit Created",
      });
      const Tenant = require("../../../models/Tenant");
      Tenant.findOne({ _id: data.tenantID }).then((tenant) => {
        User.findOne({ _id: tenant._id }).then((user) => {
          console.log(user);
          sendEmail(user);
        });
      });
    })
    .catch((error) => {
      console.log("in the error");
      dispatch({
        type: GET_ERRORS,
        payload: error.response.data,
      });
    });
};

export const display = (onDataReceived) => (dispatch) => {
  axios
    .get("/api/audits")
    .then((response) => {
      const data = response.data;
      // console.log(data);
      // console.log(data.length);
      onDataReceived(data);
    })
    .catch((err) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data,
      })
    );
};

export const updateAudit = (data) => {
  console.log(data);
  axios.put("/api/audits/update", data).catch((error) => {
    console.log(error);
  });
};

export const deleteAudit = (data) => {
  console.log(data);
  axios.post("/api/audits", data).catch((error) => {
    console.log(error);
  });
};
