import React, { Component } from "react";
import { Layout, Form, Input, Button } from "antd";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import Recaptcha from "react-recaptcha";

const { Header, Footer, Sider, Content } = Layout;

class ResetPassword extends Component {
  constructor() {
    super();
    this.verifyCallback = this.verifyCallback.bind(this);
    this.state = {
      isVerified: false,
      email: "",
    };
  }

  recaptchaLoaded() {
    console.log("captcha loaded");
  }

  verifyCallback(response) {
    if (response) {
      this.setState({ isVerified: true });
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onFinish = (values) => {
    // console.log(values);
    if (!this.state.isVerified) {
      alert("Please verify you are human");
    } else {
      window.recaptcha = null;
      var { usertype } = "";
      if (this.state.usertypebool) {
        usertype = "staff";
      } else {
        usertype = "tenant";
      }
      const userData = {
        email: values.email,
        password: values.password,
        usertype: usertype,
      };
      console.log(values);

      this.props.loginUser(userData, this.props.history); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
    }
  };

  render() {
    return (
      <Layout>
        <h2>Welcome to SingHealth Audit</h2>
        <p>
          A new password will be sent to your email address if your email
          address is valid
        </p>
        <Content>
          <Form
            onFinish={this.onFinish}
            name="normal_login"
            className="login-register-form"
          >
            <Form.Item
              name="email"
              rules={[{ required: true, message: "Please input your Email!" }]}
            >
              <Input
                placeholder="Email"
                onChange={this.onChange}
                value={this.state.email}
                id="email"
                type="email"
              />
            </Form.Item>

            <Form.Item>
              <Button
                id="reset-button"
                type="primary"
                htmlType="submit"
                className="login-register-form-button"
              >
                Reset Password
              </Button>
            </Form.Item>
          </Form>
          <Recaptcha
            sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
            render="explicit"
            onloadCallback={console.log("onloadCallback")}
            verifyCallback={this.verifyCallback}
          />
        </Content>
        <div>hello</div>
      </Layout>
    );
  }
}

ResetPassword.propTypes = {};
const mapStateToProps = (state) => ({});
export default connect(mapStateToProps, {})(ResetPassword);
