    if (type === "checkbox" && checked === true) {
      this.setState(state => state.safety_count++)
    } else {
      this.setState(state => state.safety_count--)
    }
  }

  prof_score= Math.round(10 / 2 / (prof_cat_size) * (this.state.profcount))






  render() {
