import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../../actions/authActions";
import classnames from "classnames";

import { Form, Input, Button, Checkbox, Radio } from "antd";
import { UserOutlined, LockOutlined } from "@ant-design/icons";
import axios from "axios";

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      usertypebool: false,
      errors: {},
    };
  }

  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      if (this.props.auth.user.usertype === "staff") {
        this.props.history.push("/auditlist");
      } else if (this.props.auth.user.usertype === "tenant") {
        this.props.history.push("/tenant");
      }
    }
  }
  // might be deprecated
  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      if (this.props.auth.user.usertype === "staff") {
        this.props.history.push("/auditlist"); // push staff to auditlist when they login
      } else if (this.props.auth.user.usertype === "tenant") {
        this.props.history.push("/tenant");
      }
    }
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onCheckboxChange = (e) => {
    this.setState({ usertypebool: !this.state.usertypebool });
  };

  onFinish = (values) => {
    // console.log(values);
    // Process checkbox boolean to usertype string
    var { usertype } = "";
    if (this.state.usertypebool) {
      usertype = "staff";
    } else {
      usertype = "tenant";
    }
    const userData = {
      email: values.email,
      password: values.password,
      usertype: usertype,
    };
    console.log(values);

    let promise = new Promise((resolve, reject) => {
      this.props.loginUser(userData); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
    });
    if (Object.keys(this.state.errors).length !== 0) {
      alert("Error");
    }
  };
  render() {
    const { errors } = this.state;
    return (
      <Form
        onFinish={this.onFinish}
        name="normal_login"
        className="login-register-form"
      >
        <h2>Welcome to SingHealth Audit</h2>
        <p>Enter your email and password to log in </p>
        <Form.Item
          name="email"
          rules={[{ required: true, message: "Please input your Email!" }]}
        >
          <Input
            prefix={<UserOutlined className="site-form-item-icon" />}
            placeholder="Email"
            onChange={this.onChange}
            value={this.state.email}
            error={errors.email}
            id="email"
            type="email"
            className={classnames("", {
              invalid: errors.email || errors.emailnotfound,
            })}
          />
        </Form.Item>

        <Form.Item
          name="password"
          rules={[{ required: true, message: "Please input your Password!" }]}
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            placeholder="Password"
            onChange={this.onChange}
            value={this.state.password}
            error={errors.password}
            id="password"
            type="password"
            className={classnames("", {
              invalid: errors.password || errors.passwordincorrect,
            })}
          />
        </Form.Item>

        <Form.Item name="usertypebool">
          <Checkbox
            onChange={this.onCheckboxChange}
            value={this.state.usertypebool}
            error={errors.usertypebool}
            id="usertypebool"
          >
            Singhealth Staff Member?
          </Checkbox>
        </Form.Item>

        <Form.Item>
          <Button
            id="login-button"
            type="primary"
            htmlType="submit"
            className="login-register-form-button"
          >
            Log in
          </Button>
          New staff member? <Link to="/Register">Register here!</Link>
        </Form.Item>
      </Form>
    );
  }
}
Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});
export default connect(mapStateToProps, { loginUser })(Login);
