import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authActions";
import classnames from "classnames";
import { Form, Input, Button, Checkbox } from "antd";
import { MailOutlined, UserOutlined, LockOutlined } from "@ant-design/icons";

class Register extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      email: "",
      address: "",
      password: "",
      password2: "",
      errors: {},
    };
  }

  componentDidMount() {
    // If loggied in and user navigates to Register page, should redirect them to dashboad
    if (this.props.auth.isAuthenticaed) {
      this.props.history.push("/dashboard"); // Push new entry into history stack, ie redirecting the user to another route
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };
  onFinish = (values) => {
    const newUser = {
      name: values.name,
      email: values.email,
      password: values.password,
      password2: values.password2,
    };
    console.log(newUser);
    this.props.registerUser(newUser, this.props.history);
  };

  render() {
    const { errors } = this.state;
    return (
      <Form
        name="normal_login"
        className="login-register-form"
        onFinish={this.onFinish}
      >
        <h1>Create Tenant Account</h1>

        <Form.Item
          name="name"
          rules={[
            { required: true, message: "Please input your Tenant Name!" },
          ]}
        >
          <Input
            prefix={<UserOutlined className="site-form-item-icon" />}
            placeholder="Name"
            onChange={this.onChange}
            value={this.state.name}
            error={errors.name}
            id="name"
            type="text"
            // className={classnames("", {
            // invalid: errors.name,
            // })}
          />
        </Form.Item>

        <Form.Item
          name="email"
          rules={[
            {
              required: true,
              type: "email",
              message: "Please input a valid Email!",
            },
          ]}
        >
          <Input
            prefix={<MailOutlined className="site-form-item-icon" />}
            placeholder="Email"
            onChange={this.onChange}
            value={this.state.email}
            error={errors.email}
            id="email"
            type="email"
            // className={classnames("", {
            //   invalid: errors.email,
            // })}
          />
        </Form.Item>

        <Form.Item
          name="address"
          rules={[
            {
              required: true,
              type: "string",
              message: "Please input a valid Address!",
            },
          ]}
        >
          <Input
            prefix={<MailOutlined className="site-form-item-icon" />}
            placeholder="Address"
            onChange={this.onChange}
            value={this.state.address}
            error={errors.address}
            id="address"
            type="address"
            // className={classnames("", {
            //   invalid: errors.email,
            // })}
          />
        </Form.Item>

        <Form.Item
          name="password"
          rules={[{ required: true, message: "Please input your Password!" }]}
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            placeholder="Password"
            onChange={this.onChange}
            value={this.state.password}
            error={errors.password}
            id="password"
            type="password"
            // className={classnames("", {
            //   invalid: errors.password,
            // })}
          />
        </Form.Item>

        <Form.Item
          name="password2"
          dependencies={["password"]}
          hasFeedback
          rules={[
            { required: true, message: "Passwords do not match!" },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (!value || getFieldValue("password") === value) {
                  return Promise.resolve();
                }

                return Promise.reject(
                  new Error("The two passwords that you entered do not match!")
                );
              },
            }),
          ]}
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            placeholder="Confirm Password"
            onChange={this.onChange}
            value={this.state.password2}
            error={errors.password2}
            id="password2"
            type="password"
            // className={classnames("", {
            // invalid: errors.password2,
            // })}
          />
        </Form.Item>

        <Form.Item>
          <Button
            type="primary"
            htmlType="submit"
            className="login-register-form-button"
          >
            Register
          </Button>
          Or <Link to="/Login">Login here</Link>
        </Form.Item>
      </Form>
    );
  }
}
// Props stands for properties and is being used for passing data from one component to another
Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};

// Get state from Redux and map it to props
const mapStateToProps = (state) => ({
  auth: state.auth, // allows us to call this.props.auth
  errors: state.errors, // allows us to call this.props.errors
});

export default connect(mapStateToProps, { registerUser })(withRouter(Register));
